\');\r\n\r\n\t\t\t\t\t$this->cart->clear();\r\n\r\n\t\t\t\t\t// Add to activity log\r\n\t\t\t\t\tif ($this->config->get(\'config_customer_activity\')) {\r\n\t\t\t\t\t\t$this->load->model(\'account/activity\');\r\n\r\n\t\t\t\t\t\tif ($this->customer->isLogged()) {\r\n\t\t\t\t\t\t\t$activity_data = array(\r\n\t\t\t\t\t\t\t\t\'customer_id\' => $this->customer->getId(),\r\n\t\t\t\t\t\t\t\t\'name\'        => $this->customer->getFirstName() . \' \' . $this->customer->getLastName(),\r\n\t\t\t\t\t\t\t\t\'order_id\'    => $this->session->data[\'order_id\']\r\n\t\t\t\t\t\t\t);\r\n\r\n\t\t\t\t\t\t\t$this->model_account_activity->addActivity(\'order_account\', $activity_data);\r\n\t\t\t\t\t\t} else {\r\n\t\t\t\t\t\t\t$activity_data = array(\r\n\t\t\t\t\t\t\t\t\'name\'     => $this->session->data[\'guest\'][\'firstname\'] . \' \' . $this->session->data[\'guest\'][\'lastname\'],\r\n\t\t\t\t\t\t\t\t\'order_id\' => $this->session->data[\'order_id\']\r\n\t\t\t\t\t\t\t);\r\n\r\n\t\t\t\t\t\t\t$this->model_account_activity->addActivity(\'order_guest\', $activity_data);\r\n\t\t\t\t\t\t}\r\n\t\t\t\t\t}\r\n\r\n\t\t\t\t\tunset($this->session->data[\'shipping_method\']);\r\n\t\t\t\t\tunset($this->session->data[\'shipping_methods\']);\r\n\t\t\t\t\tunset($this->session->data[\'payment_method\']);\r\n\t\t\t\t\tunset($this->session->data[\'payment_methods\']);\r\n\t\t\t\t\tunset($this->session->data[\'guest\']);\r\n\t\t\t\t\tunset($this->session->data[\'comment\']);\r\n\t\t\t\t\tunset($this->session->data[\'order_id\']);\r\n\t\t\t\t\tunset($this->session->data[\'coupon\']);\r\n\t\t\t\t\tunset($this->session->data[\'reward\']);\r\n\t\t\t\t\tunset($this->session->data[\'voucher\']);\r\n\t\t\t\t\tunset($this->session->data[\'vouchers\']);\r\n\t\t\t\t\tunset($this->session->data[\'totals\']);\r\n\r\n\t\t\t\t\tif(!$order_status[\'meta_title\']) $order_status[\'meta_title\'] = $order_status[\'name\'];\r\n\r\n\t\t\t\t\t$this->document->setTitle($order_status[\'meta_title\']);\r\n\t\t\t\t\t$this->document->setDescription($order_status[\'meta_description\']);\r\n\t\t\t\t\t$this->document->setKeywords($order_status[\'meta_keyword\']);\r\n\r\n\t\t\t\t\t$data[\'breadcrumbs\'] = array();\r\n\r\n\t\t\t\t\t$data[\'breadcrumbs\'][] = array(\r\n\t\t\t\t\t\t\'text\' => $this->language->get(\'text_home\'),\r\n\t\t\t\t\t\t\'href\' => $this->url->link(\'common/home\')\r\n\t\t\t\t\t);\r\n\r\n\t\t\t\t\t$data[\'breadcrumbs\'][] = array(\r\n\t\t\t\t\t\t\'text\' => $this->language->get(\'text_basket\'),\r\n\t\t\t\t\t\t\'href\' => $this->url->link(\'checkout/cart\')\r\n\t\t\t\t\t);\r\n\r\n\t\t\t\t\t$data[\'breadcrumbs\'][] = array(\r\n\t\t\t\t\t\t\'text\' => $this->language->get(\'text_checkout\'),\r\n\t\t\t\t\t\t\'href\' => $this->url->link(\'checkout/checkout\', \'\', true)\r\n\t\t\t\t\t);\r\n\r\n\t\t\t\t\t$data[\'breadcrumbs\'][] = array(\r\n\t\t\t\t\t\t\'text\' => $order_status[\'name\'],\r\n\t\t\t\t\t\t\'href\' => $this->url->link(\'checkout/success\')\r\n\t\t\t\t\t);\r\n\r\n\t\t\t\t\t$data[\'heading_title\'] = $order_status[\'title\'];\r\n\r\n\t\t\t\t\t$data[\'text_message\'] = ($order_status[\'description\'] ? html_entity_decode($order_status[\'description\'], ENT_QUOTES, \'UTF-8\') : $this->language->get(\'text_guest\'));\r\n\r\n\t\t\t\t\t$data[\'button_continue\'] = $this->language->get(\'button_continue\');\r\n\r\n\t\t\t\t\t$data[\'continue\'] = $this->url->link(\'common/home\');\r\n\r\n\t\t\t\t\t$data[\'column_left\'] = $this->load->controller(\'common/column_left\');\r\n\t\t\t\t\t$data[\'column_right\'] = $this->load->controller(\'common/column_right\');\r\n\t\t\t\t\t$data[\'content_top\'] = $this->load->controller(\'common/content_top\');\r\n\t\t\t\t\t$data[\'content_bottom\'] = $this->load->controller(\'common/content_bottom\');\r\n\t\t\t\t\t$data[\'footer\'] = $this->load->controller(\'common/footer\');\r\n\t\t\t\t\t$data[\'header\'] = $this->load->controller(\'common/header\');\r\n\r\n\t\t\t\t\t$this->response->setOutput($this->load->view(\'common/success\', $data))