INSERT INTO `oc_product_description` (`product_id`, `language_id`, `name`, `description`, `tag`, `meta_title`, `meta_description`, `meta_keyword`, `seo_keyword`, `seo_h1`, `seo_h2`, `seo_h3`, `image_title`, `image_alt`) VALUES ('684', '2', 'PASSIFLORA EXTRACT, 30cps, ROTTA NATURA', 'Originara de pe continentul american, passiflora (numita si floarea-pasiunii sau maracuja) este o planta agatatoare, ce poate ajunge pana la 12 m lungime. Passiflora a fost descoperita in Peru, in anul 1569, de catre medicul spaniol Monardes, care a urmarit intrebuintarea plantei la populatia indigena. Dupa intoarcerea sa in &quot;Lumea Veche&quot;, Monardes a utilizat passiflora datorita efectelor sale calmante si sedative in: anxietate, depresie, iritabilitate, insomnie, tensiune psihica si afectiuni cardiovasculare pe fond nervos.', 'passiflota,extract', 'PASSIFLORA EXTRACT, 30cps, ROTTA NATURA - 726', 'PASSIFLORA EXTRACT, 30cps, ROTTA NATURA 726  Originara de pe continentul american, passiflora (numita si floarea-pasiunii sau maracuja) este o planta agatatoare, ce poate ajunge pana la 12 m lungime. Passiflora a fost descoperita in Peru, in anul 1569, de', 'passiflota,extract', '', '', '', '', '', '');
