INSERT INTO `oc_product_description` (`product_id`, `language_id`, `name`, `description`, `tag`, `meta_title`, `meta_description`, `meta_keyword`, `seo_keyword`, `seo_h1`, `seo_h2`, `seo_h3`, `image_title`, `image_alt`) VALUES ('909', '2', 'SICURO SLIM TEA DETOX FORTE GOJI', '&lt;p&gt;Detoxifica organismul si reprezinta aliatul perfect in lupta cu kilogramele. Ceaiul pentru slabit Sicuro Slim Forte Goji este un produs 100% natural, obtinut din amestecul unor plante medicinale, fructe, frunze si radacini esentiale pentru o talie de vis.&lt;br&gt;&lt;/p&gt;', '', 'SICURO SLIM TEA DETOX FORTE GOJI - 90', 'SICURO SLIM TEA DETOX FORTE GOJI 90  Detoxifica organismul si reprezinta aliatul perfect in lupta cu kilogramele. Ceaiul pentru slabit Sicuro Slim Forte Goji este un produs 100% natural, obtinut din amestecul unor plante medicinale, fructe, frunze si rada', 'ceai de slabit, sicuro slim, forte goji, goji, detox, celulita, produs natural, antioxidant', '', '', '', '', '', '');
INSERT INTO `oc_product_description` (`product_id`, `language_id`, `name`, `description`, `tag`, `meta_title`, `meta_description`, `meta_keyword`, `seo_keyword`, `seo_h1`, `seo_h2`, `seo_h3`, `image_title`, `image_alt`) VALUES ('722', '2', 'NATURAL VITAMINA E, 100 u.i. x 30cps, ROTTA NATURA', 'Vitamina E sub forma de alfa-tocoferol s-a dovedit a fi forma cea mai activa dintre cele 8 forme existente in natura. Cunoscuta pentru puternicul efect antioxidant, se face utila in prevenirea aparitiei semnelor batranetii cauzate de stresul oxidativ. Poate proteja inima, mentine nivelul normal de colesterolul si trigliceride. Poate interveni in reglarea sistemului hormonal fiind folosita in prevenirea sterilitatii si mentinerea unei activitati sexuale normale, atat la femei cat si la barbati. Astfel, favorizeza sarcina si influenteaza potenta sexuala.', 'vitamina E,natural,antioxidant', 'NATURAL VITAMINA E, 100 u.i. x 30cps, ROTTA NATURA - 764', 'NATURAL VITAMINA E, 100 u.i. x 30cps, ROTTA NATURA 764  Vitamina E sub forma de alfa-tocoferol s-a dovedit a fi forma cea mai activa dintre cele 8 forme existente in natura. Cunoscuta pentru puternicul efect antioxidant, se face utila in prevenirea aparit', 'vitamina E,natural,antioxidant', '', '', '', '', '', '');
