INSERT INTO `oc_product_description` (`product_id`, `language_id`, `name`, `description`, `tag`, `meta_title`, `meta_description`, `meta_keyword`, `seo_keyword`, `seo_h1`, `seo_h2`, `seo_h3`, `image_title`, `image_alt`) VALUES ('742', '2', 'CARBODIGEST, 120cps, FARMACLASS', 'Carbodigest are o compozitie unica si echilibrata in substante biologic active, cu o actiune multipla, la nivelul tractului digestiv si intestinal, ducand la reglarea tranzitului, eliminarea gazelor intestinale,scaderea senzatiei de balonare. Este eficient in indigestia care este frecvent asociata cu starea de discomfort abdominal.Carbunele vegetal este cunoscut pentru capacitatea sa adsorbanta la nivelul tractului digestiv si intestinal, ceea ce duce la scaderea senzatiei de balonare, elimina gazele intestinale. Anasonul si fenicului contin uleiuri volatile cu efecte antiseptic la nivel intestinal, carminativ, eupeptic.Eficacitatea produsului este marita de adaosul suplimentar de uleiuri estentiale de mustetel, menta si fenicul ceea ce face din Carbo Digest alegerea primordiala in caz de balonare, indigestie, tranzit lent, colon irirtabil.', 'carbodigest,balonare,tranzit', 'CARBODIGEST, 120cps, FARMACLASS - 784', 'CARBODIGEST, 120cps, FARMACLASS 784  Carbodigest are o compozitie unica si echilibrata in substante biologic active, cu o actiune multipla, la nivelul tractului digestiv si intestinal, ducand la reglarea tranzitului, eliminarea gazelor intestinale,scadere', 'carbodigest,balonare,tranzit', '', '', '', '', '', '');
